\begin{align*}
 m_{*j}(A)&=\sup \left\{\,m(E):E\subset A,\, E elementary\,\right\}\\
 m^{*j}(A)&=\inf \left\{\,m(F):A\subset F,\, F elementary\,\right\} 
\end{align*}

\begin{align*}
 m(A\cup B)&=m^{*j}(A\cup B)\\
 &\leq m(F\cup F')\\
 &\leq m(F)+m(F')\\
 &<m(A)+m(B)+\epsilon 
\end{align*}

\begin{equation*}
\inf \left\{\,\operatorname {vol}(B_1)+\cdots +\operatorname {vol}(B_k)\mid B_1,\ldots ,B_k are boxes and A\subset B_1\cup \cdots \cup B_k\,\right\} 
\end{equation*}

\begin{align*}
 \underline{\int }f&=\sup \left\{\,\left.pc\!\!\int \!\! f\;\right|\;g\leq f, g pc\,\right\}\\
 \overline{\int }f&=\inf \left\{\,\left.pc\!\!\int \!\! h\;\right|\;f\leq h, h pc\,\right\} 
\end{align*}

\begin{equation*}
m^*(A)=\inf \left\{\,\left.\sum _1^\infty \operatorname {vol}(B_i)\;\right|\;B_i are boxes and A\subset \bigcup _1^\infty B_i\,\right\} 
\end{equation*}

\begin{align*}
 m^*(A)&\geq \sum \operatorname {vol}(B_i)-\epsilon \\
 &\geq m^*(\bigcup B_i)\\
 &\geq \inf \left\{\,m^*(O)\mid O is open and A\subset O\,\right\}-\epsilon 
\end{align*}

\begin{equation*}
m^*(A)=\sup \left\{\,m^*(O)\mid O is open and O\subset A\,\right\} 
\end{equation*}

\begin{align*}
 m^*(\bigcup U_n\smallsetminus \bigcup A_n) &\leq m^*(\bigcup U_n\smallsetminus A_n)\\
 &\leq \sum m^*(U_n\smallsetminus A_n)\\
 &\leq \sum \epsilon /2^n=2\epsilon 
\end{align*}

\begin{align*}
 m^*(E\bigtriangleup A)&=m^*(E\smallsetminus A)+m^*(A\smallsetminus E)\\
 &\leq m^*(O\smallsetminus A)+m^*(O\smallsetminus E)\\
 &<2\epsilon 
\end{align*}

\begin{align*}
 m(\bigcup A_n)&\geq m(\bigcup K_n)\\
 &=\sum m(K_n)\\
 &\geq \sum (m(A_n)-\epsilon /2^n)\\
 &=\sum m(A_n)-\epsilon 
\end{align*}

\begin{align*}
 m(A_1)&=m(\bigcap A_n)+m(\bigcup B_n)\\
 &=m(\bigcap A_n)+\lim m(B_n)\\
 &=m(\bigcap A_n)+\lim (m(A_1)-m(A_n))\\
 &=m(\bigcap A_n)+m(A_1)-\lim m(A_n) 
\end{align*}

\begin{align*}
 x\in f^{-1}(S)&\iff f(x)>\lambda \\
 &\iff \inf _N\sup _{n\geq N}f_n(x)>\lambda \\
 &\iff (\forall N)(\exists n\geq N)f_n(x)>\lambda \\
 &\iff x\in \bigcap _N\bigcup _{n\geq N}f_n^{-1}(\lambda ,\infty ] 
\end{align*}

\begin{equation*}
\underline{\int }f=\sup \left\{\left.s\kern -2pt\int g\;\right|\; g\leq f, g nonnegative simple\right\} 
\end{equation*}

\begin{align*}
 \int \liminf f_n&=\int \lim _N\inf _{n\geq N} f_n\\
 &=\lim _N\int \inf _{n\geq N}f_n\\
 &\leq \lim _N\inf _{n\geq N}\int f_n\\
 &=\liminf \int f_n 
\end{align*}

\begin{align*}
 &\mu ^*(S\cap (A\cup B))+\mu ^*(S\cap (A\cup B)^c)\\
 &\leq \mu ^*(S\cap A\cap B^c)+\mu ^*(S\cap A\cap B)+\mu ^*(S\cap A^c\cap B)+\mu ^*(S\cap A^c\cap B^c)\\
 &=\mu ^*(S\cap A)+\mu ^*(S\cap A^c)\\
 &=\mu (S) 
\end{align*}

\begin{align*}
 \mu ^*(S)&=\sum \mu ^*(S\cap A_n)+\mu ^*(S\smallsetminus \bigcup A_n)\\
 &\geq \mu ^*(\bigcup (S\cap A_n))+\mu ^*(S\smallsetminus \bigcup A_n)\\
 &=\mu ^*(S\cap \bigcup A_n)+\mu ^*(S\smallsetminus \bigcup A_n) 
\end{align*}

